mapper.select:
SELECT doi, title, description, metadata, created, modified, deleted, created_by, checksum, size
FROM data_package
WHERE doi = #{doi,jdbcType=OTHER}

SELECT doi, title, description, metadata, dp.created, modified, deleted, dp.created_by, checksum, size
FROM data_package dp JOIN alternative_identifier ai ON ai.data_package_doi = dp.doi
WHERE ai.identifier = #{identifier, jdbcType=OTHER}

SELECT dp.doi, dp.title, dp.description, dp.metadata, dp.created, dp.modified, dp.deleted, dp.created_by, dp.checksum, dp.size
FROM data_package dp , tag t


    AND dp.created_by=#{user,jdbcType=OTHER}


    AND dp.created  >=   #{fromDate,jdbcType=OTHER}


    AND dp.created  <=   #{toDate,jdbcType=OTHER}



      AND dp.deleted IS NOT NULL


      AND dp.deleted IS NULL



    AND t.data_package_doi = dp.doi AND t.value IN

      #{item}




  LIMIT #{page.limit} OFFSET #{page.offset}

SELECT count(*)
FROM data_package


    AND created_by=#{user,jdbcType=OTHER}


    AND created  >=   #{fromDate,jdbcType=OTHER}


    AND created  <=   #{toDate,jdbcType=OTHER}



      AND deleted IS NOT NULL


      AND deleted IS NULL




  LIMIT #{page.limit} OFFSET #{page.offset}

mapper.update:
UPDATE data_package SET
  deleted = now()
WHERE doi = #{doi, jdbcType=OTHER, typeHandler=DoiTypeHandler}
