mapper.select:
SELECT key, identifier, data_package_key, type, relation_type, created, created_by
FROM identifier
WHERE key = #{key, jdbcType=OTHER}

SELECT key, identifier, data_package_key, type, relation_type, created, created_by
FROM identifier


    AND created_by=#{user, jdbcType=OTHER}


    AND created=#{created, jdbcType=TIMESTAMP}


    AND data_package_key=#{dataPackageKey, jdbcType=OTHER}


    AND type=#{type, jdbcType=OTHER}


    AND relation_type=#{relationType, jdbcType=OTHER}


    AND identifier=#{identifier, jdbcType=OTHER}



  LIMIT #{page.limit} OFFSET #{page.offset}

SELECT count(*) FROM identifier


    AND created_by=#{user, jdbcType=OTHER}


    AND created=#{created, jdbcType=TIMESTAMP}


    AND data_package_key=#{dataPackageKey, jdbcType=OTHER}


    AND type=#{type, jdbcType=OTHER}


    AND relation_type=#{relationType, jdbcType=OTHER}


    AND identifier=#{identifier, jdbcType=OTHER}
