mapper.select:
SELECT doi, metadata, created, modified, deleted, created_by, checksum, size
FROM data_package
WHERE doi = #{doi,jdbcType=OTHER}

SELECT doi, title, description, metadata, created, modified, deleted, created_by, checksum, size
FROM data_package


    created_by=#{user,jdbcType=OTHER}


    created  >=   #{fromDate,jdbcType=OTHER}


    created  <=   #{toDate,jdbcType=OTHER}



  LIMIT #{page.limit} OFFSET #{page.offset}

SELECT count(*)
FROM data_package


    created_by=#{user,jdbcType=OTHER}


    created  >=   #{fromDate,jdbcType=OTHER}


    created  <=   #{toDate,jdbcType=OTHER}



  LIMIT #{page.limit} OFFSET #{page.offset}
